import React, {useState} from "react";
import "./index.css";

var flagsDictionary = {
  "üòä": "smiling",
  "üòí": "disbelief",
  "üòÇ": "Tears of joy",
  "‚úåüèª": "victory or friendship",
  "üëåüèª": "Looks good",
  "‚ù§Ô∏è": "love",
  "üëú": "purse"
};

var flags = Object.keys(flagsDictionary);

export default function App() {
  var [country, setCountry] = useState("");
  function onEmojiEnter(event){
    var country = flagsDictionary[event.target.value];
    setCountry(country);
  }

  function onClickEmoji(flag){
    //svar country = flags[flag];
    console.log(flag); 
    setCountry(flags[flag]);

  }
  return (
    <div className="App">
      <h1>Not sure which flag belong to which country?</h1>
      <h2>Enter or click on the flag to know</h2>
        <input style={{margin:"2rem"}} onChange={onEmojiEnter}  />
        <div>{country}</div>

     {
       flags.map(flag=>{

          return <span onClick={()=>onClickEmoji(flag)} style={{fontSize:"2rem", padding:"1rem", cursor:"pointer"}}>{flag }</span>
       })
     }


    </div>
  );
}
