import React, { useState } from "react";
import "./index.css";

var emojiDictionary = {
  "üòä": "smiling",
  "üòí": "disbelief",
  "üòÇ": "Tears of joy",
  "‚úåüèª": "victory or friendship",
  "üëåüèª": "Looks good",
  "‚ù§Ô∏è": "love",
  "üëú": "purse"
};

var emojiWeKnow = Object.keys(emojiDictionary);

export default function App() {
  var [emoji, setEmojiName] = useState("");
  function onEmojiEntryHandler(event) {
    var emoji = emojiDictionary[event.target.value];
    setEmojiName(emoji);
  }
  function onClickEmoji(emoji) {
    var meaning = emojiDictionary[emoji];
    setEmojiName(meaning);
  }
  return (
    <div className="App">
      <h1>Hello CodeSandbox</h1>
      <h2>Start editing to see some magic happen!</h2>
      <input onChange={onEmojiEntryHandler} />
      <h1> {emoji} </h1>

      <h1>The emojis we know </h1>
      {emojiWeKnow.map((item) => {
        console.log(item);
        return (
          <span
            key={item}
            onClick={() => onClickEmoji(item)}
            style={{ fontSize: "2.5rem", padding: "0.5rem", cursor: "pointer" }}
          >
            {item}
          </span>
        );
      })}
    </div>
  );
}
